import { Fragment } from "react";
import { Link, Route, useParams } from "react-router-dom";
import HighlitedQuote from "../components/quotes/HighlightedQuote";
import Comments from "../components/comments/Comments";
const QuoteDetail = () => {
  const params = useParams();
  const DUMMY_QUOTES = [
    { id: "q1", author: "Dhanu", text: "learning React is fun!" },
    { id: "q2", author: "Chandu", text: "learning React is great!" },
    {id:'q3', author :'Ullas', text : 'learning React is love!'},
  ];
  const quote = DUMMY_QUOTES.find((quote) => quote.id === params.quoteId);
  if(!quote)
  {
      return<p>No Quote Found</p>
  }
  return (
    <Fragment>
      <HighlitedQuote text={quote.text} author={quote.author} />
      <Link to='/qoutes/:quoteId/comments'> </Link>
      <Route path={`/quotes/${params.quoteId}/comments`}>
        <Comments />
      </Route>
    </Fragment>
  );
};
export default QuoteDetail;
